mapper.select:
select * from article



        category_id=#{categoryId}



        and state=#{state}



        and auditing_state=#{auditingState}

select
*,
(likes * 5 + view * 1 - TIMESTAMPDIFF(DAY , update_time, NOW()) * 3) as weighted_rank
from article


        and category_id=#{categoryId}


        and state=#{state}


        and auditing_state=#{auditingState}


order by weighted_rank desc

select * from article

    create_user=#{userId}

        and category_id=#{categoryId}



        and state=#{state}


ORDER BY article_id DESC
